import React, { useEffect, useState } from 'react';
import { Link, useParams } from 'react-router-dom';
import axios from "axios";
function ProductSec(){
    const [info,setprod]=useState([]);
    useEffect(() => {
        axios.get(`http://localhost:3001/getproducts`)

            .then(res => setprod(res.data))
            .catch(err => console.log(err));
    }, []);
    return (
        <section id='product'>
            <div className='shopprod'>
                <div className='prod'>
                    {info.map((product) => (
                        <Link to={ `/brand/${product._id}`}key={product._id}>
                            <div className="camera">
                                <img className="brandy" src={`./images/${product.image}`} alt={product.name} />
                                <div className='des'><h4 className='nam'>{product.brands.toUpperCase()}</h4></div>

                            </div>
                        </Link>
                    ))}
                </div>
            </div>
        </section>
    )
}
export default ProductSec;
