import React, { useEffect, useState } from 'react';
import { Link, useParams } from 'react-router-dom';
import axios from "axios";
import "./styles.css"
import Navbar from './nav';
function ProductDet(){
    const { id } = useParams();
    const [info,setProduct]=useState([]);
    useEffect(()=>{
        axios.get(`http://localhost:3001/getidproducts/${id}`)
        .then(res=>setProduct(res.data))
        .catch(err=>console.log(err))
    },[id]);
    return(
        <section id='pro'>
        <Navbar/>
            <div className='s'>
                <div className='p'>
                
                    { info.map((product) => (
                        <div>
                        <div className="det">
                                <img className="brand2" src={`../images/${product.img}`} alt={product.name} />
                                
                            </div>
                            <div className="infor"><p>{product.brand} {product.name}</p></div>
                            <div className="price"><p>${product.price}</p></div>
                            <div className='caminfo'><p>{product.desc}</p></div>
                        </div>


                        
                    ))}
                </div>
            </div>
        </section>

    )

}   
export default ProductDet;
