// ProductDet.js
import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import './styles.css';
import Navbar from './nav';
import Cart from './cart';

function ProductDet() {
  const { id } = useParams();
  const [info, setProduct] = useState([]);
  const [count, setCount] = useState(1);
  const [isCartOpen, setIsCartOpen] = useState(false);

  useEffect(() => {
    axios
      .get(`http://localhost:3001/getidproducts/${id}`)
      .then((res) => setProduct(res.data))
      .catch((err) => console.log(err));
  }, [id]);

  const increase = () => setCount(count + 1);

  const decrease = () => setCount(count > 0 ? count - 1 : 0);

  const handleAddToCart = () => {
    if (info.length > 0) {
      // Assuming info contains only one product for simplicity
      const product = info[0];
      Cart.addToCart(product);
    }
  };

  return (
    <section id='pro'>
      <Navbar />
      <div className='s'>
        <div className='p'>
          {info.map((product) => (
            <div key={product.id} className='box'>
              <div className='det'>
                <img className='brand3' src={`../images/${product.img}`} alt={product.name} />
              </div>
              <div className='detail'>
                <div className='descrip'>
                  <p>
                    {product.brand} {product.name}
                  </p>
                </div>
                <div className='descrip'>
                  <p>{product.desc}</p>
                </div>
                <div className='prices'>
                  <p>${product.price}</p>
                </div>
                <div className='container'>
                  <h1>{count}</h1>
                  <button onClick={decrease}>-</button>
                  <button onClick={increase}>+</button>
                </div>
                <button onClick={handleAddToCart}>Add to Cart</button>
              </div>
            </div>
          ))}
        </div>
      </div>
      {/* Pass the cart state and a function to close the cart to the Cart component */}
      <Cart isOpen={isCartOpen} closeCart={() => setIsCartOpen(false)} />
    </section>
  );
}

export default ProductDet;
