const Banner = ({ id, text, index }) => {
    const [{ isDragging }, drag] = useDrag({
      type: 'BANNER',
      item: { id, type: 'BANNER', index },
      collect: monitor => ({
        isDragging: !!monitor.isDragging(),
      }),
    });
  
    const cardStyle = {
      opacity: isDragging ? 0.5 : 1,
      cursor: 'move',
      border: '1px solid #ccc',
      padding: '10px',
      marginBottom: '10px',
      backgroundColor: '#f9f9f9',
      position: 'relative',
      zIndex: index, // Ensure the dragged card is on top
      marginLeft: `${index * 20}px`, // Adjust the horizontal position
    };
  
    return (
      <div ref={drag} style={cardStyle}>
        {text}
      </div>
    );
  };
export default Banner
