import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import axios from "axios";
import "./styles.css"
function Brand() {
    const [info, setdata] = useState([]);
    
    useEffect(() => {
        axios.get('http://localhost:3001/getbrands')
            .then(res => setdata(res.data))
            .catch(err => console.log(err));
    }, []);

    return (
        <section id='brand'>
            <div className='shopbrand'>
                <div className='brand'>
                    {info.data && info.data.map((product) => (
                        <Link to={ `/Brands/${product._id}`}key={product._id}>
                            <div className="camera">
                                <img className="brand1" src={`./images/${product.ime}`} alt={product.name} />
                                <div className='des'><h4 className='nam'>{product.brand.toUpperCase()}</h4></div>
                                {/* <div className={product.brand.toLowerCase()}><h4>{product.brand}</h4></div>
                                <div className="price"><p>${product.price}</p></div> */}
                            </div>
                        </Link>
                    ))}
                </div>
            </div>
        </section>
    )
}

export default Brand;
