import React, { useEffect, useState } from 'react';
import { Link, useParams } from 'react-router-dom';
import axios from "axios";
import "./styles.css"
function Products() {
    const [info, setdata] = useState([]);
    const {id}=useParams();
    
    useEffect(() => {
        axios.get('http://localhost:3001/getproducts?_id=${id}')
            .then(res => setdata(res.data))
            .catch(err => console.log(err));
    }, []);
   
        return (
            <section id='pro'>
                <div className='s'>
                    <div className='p'>
                    <h2>Popular Products</h2>
                        {info.data && info.data.map((product) => (
                            <Link to={ `/product/${product._id}`}key={product._id}>
                                <div className="it">
                                    <img className="brand1" src={`./images/${product.img}`} alt={product.name} />
                                </div>
                                <div className="caminfo"><p>{product.brand} {product.name}</p></div>
                                <div className="price"><p>${product.price}</p></div>

                            </Link>
                        ))}
                    </div>
                </div>
            </section>
        )
    }

    
export default Products;
