import React, { useEffect, useState } from 'react';
import { Link, useParams } from 'react-router-dom';
import axios from "axios";
import "./styles.css"
import Navbar from './nav';
function Products() {
    const [info, setdata] = useState([]);
    
    
    useEffect(() => {
        axios.get(`http://localhost:3001/getproducts?nikon`)

            .then(res => setdata(res.data))
            .catch(err => console.log(err));
    }, []);
   
        return (
            <section id='pro'>
            <Navbar/>
                <div className='s'>
                    <div className='p'>
                    <h2>Popular Products</h2>
                        { info.map((pro) => (
                            <Link to={ `/product/${pro._id}`}key={pro._id}>
                                <div className="it">
                                    <img className="brand1" src={`./images/${pro.img}`} alt={pro.name} />
                                </div>
                                <div className="caminfo"><p>{pro.brand} {pro.name}</p></div>
                                <div className="price"><p>${pro.price}</p></div>

                            </Link>
                        ))}
                    </div>
                </div>
            </section>
        )
    }

    
export default Products;
