import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import axios from "axios";
import "./styles.css"
function Products() {
    const [info, setdata] = useState([]);
    
    useEffect(() => {
        axios.get('http://localhost:3001/getproducts')
            .then(res => setdata(res.data))
            .catch(err => console.log(err));
    }, []);
   
        return (
            <section id='pro'>
                <div className='s'>
                    <div className='p'>
                        {info.data && info.data.map((product) => (
                            <Link to={ `/product/${product._id}`}key={product._id}>
                                <div className="">
                                    <img className="brand1" src={`./images/${product.img}`} alt={product.name} />
                                    <div className='des'><h4 className='nam'>{product.brand.toUpperCase()}</h4></div>
                                    <div className={product.brand.toLowerCase()}><h4>{product.brand}</h4></div>
                                    <div className="price"><p>${product.price}</p></div>
                                </div>
                            </Link>
                        ))}
                    </div>
                </div>
            </section>
        )
    }

    
export default Products;
